package javaBasics;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i=0;
		//print number from 0 to 100 using while loop
		while(i<=100)
		{
			System.out.println(i);
			i++;
		}
		//print number from 100 to 0 in decreasing order using for loop
		for (int j=100;j>=0;j--)
		{
			System.out.println(j);
		}
	}

}
